import React, { useRef, useEffect, useState } from "react";
import Canvas from "./Canvas";
import "./App.css";

function App() {
  const canvasSize = 1000;

  let [chipCoords, setChipCoords] = useState([]);
  const chipsPerRow = 4;
  const chipRad = 50;

  const player1chip = [chipRad + 20, chipRad + 20];
  const player2chip = [canvasSize - player1chip[0], player1chip[1]];

  const drawRegionLeft = 200;
  const drawRegionRight = canvasSize - drawRegionLeft;
  const drawRegionTop = 300;
  const drawRegionBottom = canvasSize - 100;

  chipCoords.push(player1chip);
  chipCoords.push(player2chip);

  // prettier-ignore
  for (let i = drawRegionLeft; i <= drawRegionRight; i += (drawRegionRight - drawRegionLeft) / (chipsPerRow - 1)) {
    for (let j = drawRegionTop; j <= drawRegionBottom; j += (drawRegionBottom - drawRegionTop) / (chipsPerRow - 1)) {
      chipCoords = chipCoords.concat([[i, j, false]]);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <Canvas
          canvasSize={canvasSize}
          chipCoords={chipCoords}
          chipRad={chipRad}
          drawRegionLeft={drawRegionLeft}
          drawRegionRight={drawRegionRight}
          drawRegionTop={drawRegionTop}
          drawRegionBottom={drawRegionBottom}
        ></Canvas>
      </header>
    </div>
  );
}

export default App;
