import React, { useRef, useEffect, useState } from "react";

function Canvas(props) {
  let canvas;
  const canvasWidth = props.canvasWidth;
  const canvasHeight = props.canvasHeight;
  const canvasRef = useRef(null);

  const [canvasLeftEdge, setCanvasLeftEdge] = useState(0);
  const [canvasTopEdge, setCanvasTopEdge] = useState(0);

  const drawRegionLeft = props.drawRegionLeft;
  const drawRegionRight = props.drawRegionRight;
  const drawRegionTop = props.drawRegionTop;
  const drawRegionBottom = props.drawRegionBottom;

  const [chipCoords, setChipCoords] = useState([]);
  const chipsPerRow = 7;
  const chipsPerCol = 6;
  const chipRad = 50;

  const redChipColor = "#900000";
  const yellowChipColor = "#fbec5d";

  const newDrawRegionLeft = drawRegionLeft - chipRad - 40;
  const newDrawRegionTop = drawRegionTop - chipRad - 40;
  const w = drawRegionRight - drawRegionLeft + 2 * (chipRad + 40);
  const h = drawRegionBottom - drawRegionTop + 2 * (chipRad + 40);

  const [player, setPlayer] = useState(1);
  const [mouseX, setMouseX] = useState(0);
  const [mouseY, setMouseY] = useState(0);

  const setUp = (ctx) => {
    ctx.fillStyle = "#DCDCDC";
    ctx.fillRect(0, 0, canvasWidth, canvasHeight);

    // prettier-ignore
    for (let i = drawRegionLeft; i <= drawRegionRight; i += (drawRegionRight - drawRegionLeft) / (chipsPerRow - 1)) {
      for (let j = drawRegionTop; j <= drawRegionBottom; j += (drawRegionBottom - drawRegionTop) / (chipsPerCol - 1)) {
        const newChipCoords = chipCoords;
        newChipCoords.push([i, j, "#DCDCDC"]);
        setChipCoords(newChipCoords);
      }
    }
  };

  // prettier-ignore
  const drawBoard = (ctx, color, topLeftX, topLeftY, w, h) => {
    ctx.fillStyle = color;
    ctx.fillRect(topLeftX, topLeftY, w, h);
  };

  const drawChips = (ctx) => {
    for (const coord of chipCoords) {
      drawChip(ctx, coord[2], coord[0], coord[1], chipRad);
    }
  };

  const drawChip = (ctx, color, x, y) => {
    ctx.fillStyle = color;
    ctx.beginPath();
    ctx.arc(x, y, chipRad, 0, 2 * Math.PI);
    ctx.fill();
    ctx.stroke();
  };

  const handleMouseDown = (event) => {
    event.preventDefault();
    event.stopPropagation();

    setMouseX(parseInt(event.clientX - canvasLeftEdge));
    setMouseY(parseInt(event.clientY - canvasTopEdge));
  };

  const drop = (chipColor, clickX, clickY, rectLeft, rectRight) => {
    let newChipCoords = chipCoords;

    for (
      let i = drawRegionLeft;
      i < drawRegionRight;
      i += (drawRegionRight - drawRegionLeft) / (chipsPerRow - 1)
    ) {
      const leftBoundary = i;
      const rightBoundary =
        i + (drawRegionRight - drawRegionLeft) / (chipsPerRow - 1);
      if (
        clickX >= leftBoundary &&
        clickX < rightBoundary &&
        clickY >= drawRegionBottom &&
        clickY <= drawRegionTop
      ) {
        newChipCoords[5][2] = chipColor;
      }
    }

    if (chipX >= rectLeft && chipX < rectLeft + rectRight / 4) {
      newChipCoords[3][3] = true;
      setChipCoords(newChipCoords);
    } else if (
      chipX >= rectLeft + rectRight / 4 &&
      chipX < rectLeft + rectRight / 2
    ) {
      newChipCoords[7][3] = true;
      setChipCoords(newChipCoords);
    } else if (
      chipX >= rectLeft + rectRight / 2 &&
      chipX < rectLeft + (3 * rectRight) / 4
    ) {
      newChipCoords[11][3] = true;
      setChipCoords(newChipCoords);
    } else if (chipX >= rectLeft + (3 * rectRight) / 4 && chipX <= rectRight) {
      newChipCoords[15][3] = true;
      setChipCoords(newChipCoords);
    }
  };

  const handleMouseUp = (event) => {
    event.preventDefault();
    event.stopPropagation();

    if (player === 1) {
      drop(redChipColor);
    } else if (player === 2) {
      drop(yellowChipColor);
    }
  };

  const handleMouseOut = (event) => {
    event.preventDefault();
    event.stopPropagation();
    handleMouseUp(event);
  };

  useEffect(() => {
    canvas = canvasRef.current;
    let ctx = canvas.getContext("2d");

    const rect = canvas.getBoundingClientRect();
    console.log("Bounding rect: " + rect.left);
    setCanvasLeftEdge(rect.left);
    setCanvasTopEdge(rect.top);

    setUp(ctx);
    drawBoard(ctx, "#0099FF", newDrawRegionLeft, newDrawRegionTop, w, h);
  }, []);

  useEffect(() => {
    canvas = canvasRef.current;
    let ctx = canvas.getContext("2d");

    console.log("Redrawing...");

    drawChips(ctx);
  }, [chipCoords]);

  return (
    <div className="canvas">
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
      ></canvas>
    </div>
  );
}

export default Canvas;
