import logo from "./logo.svg";
import Canvas from "./Canvas";
import "./App.css";

function App() {
  const canvasSize = 800;

  const chipCoords = [];
  const chipsPerRow = 4;
  const chipRad = 50;

  const drawRegionStart = 150;
  const drawRegionEnd = canvasSize - drawRegionStart;

  // prettier-ignore
  for (let i = drawRegionStart; i <= drawRegionEnd; i += (drawRegionEnd - drawRegionStart) / (chipsPerRow - 1)) {
    for (let j = drawRegionStart; j <= drawRegionEnd; j += (drawRegionEnd - drawRegionStart) / (chipsPerRow - 1)) {
      chipCoords.push([i, j]);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <Canvas
          canvasSize={canvasSize}
          chipCoords={chipCoords}
          chipRad={chipRad}
        ></Canvas>
      </header>
    </div>
  );
}

export default App;
