import React, { useRef, useEffect, useState } from "react";

function Canvas(props) {
  let canvas;
  const canvasSize = props.canvasSize;
  const canvasRef = useRef(null);

  let chipCoords = props.chipCoords;
  const chipRad = props.chipRad;

  const drawRegionLeft = props.drawRegionLeft;
  const drawRegionRight = props.drawRegionRight;
  const drawRegionTop = props.drawRegionTop;
  const drawRegionBottom = props.drawRegionBottom;

  console.log(chipCoords[15]);
  console.log(drawRegionBottom - chipRad - 20);

  const draw = (ctx, canvasDim) => {
    ctx.fillStyle = "#DCDCDC";
    ctx.fillRect(0, 0, canvasDim, canvasDim);

    drawBoard(
      ctx,
      "#0099FF",
      drawRegionLeft - chipRad - 40,
      drawRegionTop - chipRad - 20,
      drawRegionRight - chipRad - 40,
      drawRegionBottom - chipRad - 20
    );
    for (const coord of chipCoords) {
      drawChip(ctx, "#900000", coord[0], coord[1], props.chipRad);
    }
    console.log(canvasSize - 300);
  };

  const drawChip = (ctx, color, x, y, rad) => {
    ctx.fillStyle = color;
    ctx.beginPath();
    ctx.arc(x, y, chipRad, 0, 2 * Math.PI);
    ctx.fill();
  };

  // prettier-ignore
  const drawBoard = (ctx, color, topLeftX, topLeftY, bottomRightX, bottomRightY) => {
    ctx.fillStyle = color;
    ctx.fillRect(topLeftX, topLeftY, bottomRightX, bottomRightY);
  };

  useEffect(() => {
    console.log("Drawing");
    canvas = canvasRef.current;
    let ctx = canvas.getContext("2d");

    canvas.width = canvasSize;
    canvas.height = canvasSize;

    draw(ctx, canvasSize);
  }, [draw]);

  return (
    <div className="canvas">
      <canvas ref={canvasRef} {...props}></canvas>
    </div>
  );
}

export default Canvas;
