import logo from "./logo.svg";
import Canvas from "./Canvas";
import "./App.css";

function App() {
  const canvasSize = 1000;

  const chipCoords = [];
  const chipsPerRow = 4;
  const chipRad = 50;

  const player1chip = [chipRad + 20, chipRad + 20];
  const player2chip = [canvasSize - player1chip[0], player1chip[1]];

  const drawRegionStart = 300;
  const drawRegionEnd = canvasSize - 100;

  chipCoords.push(player1chip);
  chipCoords.push(player2chip);

  // prettier-ignore
  for (let i = drawRegionStart; i <= drawRegionEnd; i += (drawRegionEnd - drawRegionStart) / (chipsPerRow - 1)) {
    for (let j = drawRegionStart; j <= drawRegionEnd; j += (drawRegionEnd - drawRegionStart) / (chipsPerRow - 1)) {
      chipCoords.push([i, j]);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <Canvas
          canvasSize={canvasSize}
          chipCoords={chipCoords}
          chipRad={chipRad}
        ></Canvas>
      </header>
    </div>
  );
}

export default App;
