import React, { useRef, useEffect, useState } from "react";

function Canvas(props) {
  let canvas;
  const canvasRef = useRef(null);

  let chipCoords = props.chipCoords;
  const chipRad = 50;

  const draw = (ctx, canvasSize) => {
    ctx.fillStyle = "#DCDCDC";
    ctx.fillRect(0, 0, canvasSize, canvasSize);

    drawChip(ctx, "#900000", 350, 350, props.chipRad);
  };

  const drawChip = (ctx, color, x, y, rad) => {
    ctx.fillStyle = color;
    ctx.beginPath();
    ctx.arc(x, y, chipRad, 0, 2 * Math.PI);
    ctx.fill();
  };

  useEffect(() => {
    console.log("Drawing");
    canvas = canvasRef.current;
    let ctx = canvas.getContext("2d");

    canvas.width = props.canvasSize;
    canvas.height = props.canvasSize;

    draw(ctx, canvasSize);
  }, [draw]);

  return (
    <div className="canvas">
      <canvas ref={canvasRef} {...props}></canvas>
    </div>
  );
}

export default Canvas;
