import React, { useRef, useEffect, useState } from "react";

function Canvas(props) {
  let canvas;
  const canvasSize = props.canvasSize;
  const canvasRef = useRef(null);

  let chipCoords = props.chipCoords;
  const chipRad = props.chipRad;

  const drawRegionLeft = props.drawRegionLeft;
  const drawRegionRight = props.drawRegionRight;
  const drawRegionTop = props.drawRegionTop;
  const drawRegionBottom = props.drawRegionBottom;

  console.log(drawRegionRight);
  console.log(drawRegionLeft);
  console.log(drawRegionBottom);
  console.log(drawRegionTop);

  const draw = (ctx, canvasDim) => {
    ctx.fillStyle = "#DCDCDC";
    ctx.fillRect(0, 0, canvasDim, canvasDim);

    const newDrawRegionLeft = drawRegionLeft - chipRad - 40;
    const newDrawRegionTop = drawRegionTop - chipRad - 40;
    const w = drawRegionRight - drawRegionLeft + 2 * (chipRad + 40);
    const h = drawRegionBottom - drawRegionTop + 2 * (chipRad + 40);

    drawBoard(ctx, "#0099FF", newDrawRegionLeft, newDrawRegionTop, w, h);
    for (const coord of chipCoords) {
      drawChip(ctx, "#DCDCDC", coord[0], coord[1], props.chipRad);
    }
    drawChip(ctx, "#900000", chipCoords[0][0], chipCoords[0][1], props.chipRad);
    drawChip(ctx, "#FFFF33", chipCoords[1][0], chipCoords[1][1], props.chipRad);
    for (const coord of chipCoords) {
      //drawChip(ctx, "#900000", coord[0], coord[1], props.chipRad);
    }
  };

  const drawChip = (ctx, color, x, y, rad) => {
    ctx.fillStyle = color;
    ctx.beginPath();
    ctx.arc(x, y, chipRad, 0, 2 * Math.PI);
    ctx.fill();
    ctx.stroke();
  };

  // prettier-ignore
  const drawBoard = (ctx, color, topLeftX, topLeftY, w, h) => {
    ctx.fillStyle = color;
    ctx.fillRect(topLeftX, topLeftY, w, h);
  };

  useEffect(() => {
    canvas = canvasRef.current;
    let ctx = canvas.getContext("2d");

    canvas.width = canvasSize;
    canvas.height = canvasSize;

    draw(ctx, canvasSize);
  }, [draw]);

  return (
    <div className="canvas">
      <canvas ref={canvasRef} {...props}></canvas>
    </div>
  );
}

export default Canvas;
