import React, { useRef, useEffect, useState } from "react";

function Canvas(props) {
  let canvas;
  const canvasSize = props.canvasSize;
  const canvasRef = useRef(null);

  let chipCoords = props.chipCoords;
  const chipRad = props.chipRad;

  const draw = (ctx, canvasDim) => {
    ctx.fillStyle = "#DCDCDC";
    ctx.fillRect(0, 0, canvasDim, canvasDim);

    for (const coord of chipCoords) {
      drawChip(ctx, "#900000", coord[0], coord[1], props.chipRad);
    }
    console.log(canvasSize - 300);
    drawBoard(ctx, "#0099FF", 300, 300);
  };

  const drawChip = (ctx, color, x, y, rad) => {
    ctx.fillStyle = color;
    ctx.beginPath();
    ctx.arc(x, y, chipRad, 0, 2 * Math.PI);
    ctx.fill();
  };

  const drawBoard = (ctx, color, topLeftX, topLeftY) => {
    ctx.fillStyle = color;
    ctx.fillRect(
      topLeftX,
      topLeftY,
      canvasSize - topLeftX,
      canvasSize - topLeftY
    );
  };

  useEffect(() => {
    console.log("Drawing");
    canvas = canvasRef.current;
    let ctx = canvas.getContext("2d");

    canvas.width = canvasSize;
    canvas.height = canvasSize;

    draw(ctx, canvasSize);
  }, [draw]);

  return (
    <div className="canvas">
      <canvas ref={canvasRef} {...props}></canvas>
    </div>
  );
}

export default Canvas;
